import numpy as np

def create_matrix_from_data(data, width):
    """
    Creates a 2D matrix from a given data string.

    Args:
        data (str): The input string of data.
        width (int): The width of the matrix.

    Returns:
        np.array: A 2D numpy array representing the matrix.
    """
    # Calculate the number of rows required based on the width
    height = len(data) // width
    # Truncate any extra characters that won't fit in the matrix
    trimmed_data = data[:height * width]
    # Convert to a matrix
    matrix = np.array(list(trimmed_data)).reshape((height, width))
    return matrix

def display_matrix(matrix):
    """
    Displays the matrix in a readable format.

    Args:
        matrix (np.array): The 2D matrix to display.
    """
    for row in matrix:
        print(" ".join(row))

def twist_and_expand_matrix(matrix):
    """
    Applies a "twist" transformation by shifting rows or columns
    and expands the matrix by duplicating the pattern.

    Args:
        matrix (np.array): The 2D matrix to transform.

    Returns:
        np.array: The transformed matrix.
    """
    # Apply a simple shift to rows as a "twist" example
    twisted_matrix = np.roll(matrix, shift=1, axis=1)
    # Expand the matrix by duplicating rows and columns
    expanded_matrix = np.vstack([twisted_matrix, twisted_matrix])
    expanded_matrix = np.hstack([expanded_matrix, expanded_matrix])
    return expanded_matrix

